#include "arvo_rkp.h"
#include "roccat_helper.h"
#include "g_roccat_helper.h"
#include "i18n-lib.h"

static guint8 const default_rkp_data[] = {
	"[Setting]\n"
	"ProFileName=Default\n" /* non standard */
	"Mode=1\n"
	"MediaOption=0\n"
	"key0=0\n"
	"key1=0\n"
	"key2=0\n"
	"key3=0\n"
	"key4=1\n"
	"\n"
	"\n"
	"\n"
	"\n"
	"\n"
	"GameFile0=\n"
	"GameFile1=\n"
	"GameFile2=\n"
};

static ArvoRkp *arvo_rkp_new(void) {
	ArvoRkp *rkp = g_malloc0(sizeof(ArvoRkp));
	rkp->key_file = g_key_file_new();
	return rkp;
}

void arvo_rkp_free(ArvoRkp *rkp) {
	if (rkp) {
		g_key_file_free(rkp->key_file);
		g_free(rkp);
	}
}

ArvoRkp *arvo_rkp_dup(ArvoRkp const *src) {
	ArvoRkp *dest = g_malloc0(sizeof(ArvoRkp));
	guint i;

	dest->key_file = roccat_key_file_dup(src->key_file);
	dest->modified_rkp = src->modified_rkp;
	dest->modified_key_mask = src->modified_key_mask;
	dest->modified_mode_key = src->modified_mode_key;
	for (i = 0; i < ARVO_BUTTON_NUM; ++i)
		dest->modified_buttons[i] = src->modified_buttons[i];

	return dest;
}

ArvoRkp *arvo_rkp_read_with_path(gchar const *path, GError **error) {
	ArvoRkp *rkp = arvo_rkp_new();

	if (!g_key_file_load_from_file(rkp->key_file, path, G_KEY_FILE_NONE, error)) {
		arvo_rkp_free(rkp);
		return NULL;
	}
	return rkp;
}

gboolean arvo_rkp_write_with_path(gchar const *path, ArvoRkp *rkp, GError **error) {
	gchar *data;
	gsize size;
	gboolean ret;

	data = g_key_file_to_data(rkp->key_file, &size, error);
	if (!data)
		goto error;

	ret = roccat_profile_write_with_path(path, data, size, error);
	g_free(data);
	if (!ret)
		goto error;

	return TRUE;
error:
	g_critical(_("Could not write rkp %s: %s"), path, (*error)->message);
	return FALSE;
}

ArvoRkp const *arvo_default_rkp(void) {
	static ArvoRkp *rkp = NULL;
	GError *error = NULL;

	if (rkp == NULL) {
		rkp = arvo_rkp_new();

		if (!g_key_file_load_from_data(rkp->key_file, (gchar const *)default_rkp_data, sizeof(default_rkp_data), 0, &error)) {
			g_clear_pointer(&rkp, arvo_rkp_free);
			g_critical(_("Could not create default rkp: %s"), error->message);
			g_error_free(error);
		}
	}

	return rkp;
}

void arvo_linux_rkp_to_windows(ArvoRkp *rkp) {
	ArvoRkpButtonInfo *button_info;
	guint i;

	for (i = 0; i < ARVO_BUTTON_NUM; ++i) {
		button_info = arvo_rkp_get_button_info(rkp, i);
		if (button_info->type == ARVO_RKP_BUTTON_INFO_TYPE_MACRO) {
			button_info->ns_loop = 0;
			arvo_rkp_set_button_info(rkp, i, button_info);
		}
		arvo_rkp_button_info_free(button_info);
	}
}

void arvo_windows_rkp_to_linux(ArvoRkp *rkp) {
	ArvoRkpButtonInfo *button_info;
	guint i;

	for (i = 0; i < ARVO_BUTTON_NUM; ++i) {
		button_info = arvo_rkp_get_button_info(rkp, i);
		if (button_info->type == ARVO_RKP_BUTTON_INFO_TYPE_MACRO) {
			if (button_info->ns_loop != 0)
				g_message(_("ns_loop == %d, should be 0"), button_info->ns_loop);
			button_info->ns_loop = 1;
			arvo_rkp_set_button_info(rkp, i, button_info);
		}
		arvo_rkp_button_info_free(button_info);
	}
}
