static KoneRMP *kone_rmp_new(void) {
	KoneRMP *rmp = g_malloc0(sizeof(KoneRMP));
	rmp->key_file = g_key_file_new();
	return rmp;
}

KoneRMP *kone_rmp_read_with_path(gchar const *path, GError **error) {
	KoneRMP *rmp = kone_rmp_new();

	if (!g_key_file_load_from_file(rmp->key_file, path, G_KEY_FILE_NONE, error))
		return NULL;

	return rmp;
}

gboolean kone_rmp_write_with_path(gchar const *path, KoneRMP *rmp, GError **error) {
	gchar *data;
	gsize size;
	gboolean ret;

	data = g_key_file_to_data(rmp->key_file, &size, error);
	if (!data)
		goto error;

	ret = roccat_profile_write_with_path(path, data, size, error);
	g_free(data);
	if (!ret)
		goto error;

	return TRUE;
error:
	g_warning(_("Could not write rmp %s: %s"), path, (*error)->message);
	return FALSE;
}

void kone_rmp_free(KoneRMP *rmp) {
	if (rmp) {
		g_key_file_free(rmp->key_file);
		g_free(rmp);
	}
}

KoneRMP *kone_rmp_dup(KoneRMP const *src) {
	KoneRMP *dest = g_malloc0(sizeof(KoneRMP));

	dest->key_file = roccat_key_file_dup(src->key_file);
	dest->modified_profile = src->modified_profile;
	dest->modified_rmp = src->modified_rmp;

	return dest;
}

KoneRMP const *kone_default_rmp(void) {
	static KoneRMP *rmp = NULL;
	GError *error = NULL;

	if (rmp == NULL) {
		rmp = kone_rmp_new();

		if (!g_key_file_load_from_data(rmp->key_file, (gchar const *)default_rmp_data, sizeof(default_rmp_data), 0, &error)) {
			g_clear_pointer(&rmp, kone_rmp_free);
			g_critical(_("Could not create default rmp: %s"), error->message);
			g_error_free(error);
		}
	}

	return rmp;
}

void kone_linux_rmp_to_windows(KoneRMP *rmp) {
	KoneRMPButtonInfo *button_info;
	guint i;

	kone_rmp_remove_profile_name(rmp);

	for (i = 0; i < KONE_BUTTON_INFO_NUM; ++i) {
		button_info = kone_rmp_get_rmp_button_info(rmp, i);
		if (button_info->type == KONE_BUTTON_INFO_TYPE_MACRO) {
			button_info->ns_loop = 0;
			kone_rmp_set_rmp_button_info(rmp, i, button_info);
		}
		g_free(button_info);
	}
}

void kone_windows_rmp_to_linux(KoneRMP *rmp) {
	KoneRMPButtonInfo *button_info;
	guint i;

	kone_rmp_set_profile_name(rmp, "");

	for (i = 0; i < KONE_BUTTON_INFO_NUM; ++i) {
		button_info = kone_rmp_get_rmp_button_info(rmp, i);
		if (button_info->type == KONE_BUTTON_INFO_TYPE_MACRO) {
			if (button_info->ns_loop != 0)
				g_message(_("ns_loop == %d, should be 0"), button_info->ns_loop);
			button_info->ns_loop = 1;
			kone_rmp_set_rmp_button_info(rmp, i, button_info);
		}
		g_free(button_info);
	}
}
