static KovaplusRmp *kovaplus_rmp_new(void) {
	KovaplusRmp *rmp = g_malloc0(sizeof(KovaplusRmp));
	rmp->key_file = g_key_file_new();
	return rmp;
}

void kovaplus_rmp_free(KovaplusRmp *rmp) {
	if (rmp) {
		g_key_file_free(rmp->key_file);
		g_free(rmp);
	}
}

KovaplusRmp *kovaplus_rmp_dup(KovaplusRmp const *src) {
	KovaplusRmp *dest = g_malloc(sizeof(KovaplusRmp));

	dest->key_file = roccat_key_file_dup(src->key_file);
	dest->modified_rmp = src->modified_rmp;
	dest->modified_profile_buttons = src->modified_profile_buttons;
	dest->modified_profile_settings = src->modified_profile_settings;

	return dest;
}

KovaplusRmp *kovaplus_rmp_read_with_path(gchar const *path, GError **error) {
	KovaplusRmp *rmp = kovaplus_rmp_new();

	if (!g_key_file_load_from_file(rmp->key_file, path, G_KEY_FILE_NONE, error)) {
		kovaplus_rmp_free(rmp);
		return NULL;
	}

	return rmp;
}

gboolean kovaplus_rmp_write_with_path(gchar const *path, KovaplusRmp *rmp, GError **error) {
	gchar *data;
	gsize size;
	gboolean ret;

	data = g_key_file_to_data(rmp->key_file, &size, error);
	if (!data)
		goto error;

	ret = roccat_profile_write_with_path(path, data, size, error);
	g_free(data);
	if (!ret)
		goto error;

	return TRUE;
error:
	g_warning(_("Could not write rmp %s: %s"), path, (*error)->message);
	return FALSE;
}

KovaplusRmp const *kovaplus_default_rmp(void) {
	static KovaplusRmp *rmp = NULL;
	GError *error = NULL;

	if (rmp == NULL) {
		rmp = kovaplus_rmp_new();

		if (!g_key_file_load_from_data(rmp->key_file, (gchar const *)default_rmp_data, sizeof(default_rmp_data), 0, &error)) {
			g_clear_pointer(&rmp, kovaplus_rmp_free);
			g_critical(_("Could not create default rmp: %s"), error->message);
			g_error_free(error);
		}
	}

	return rmp;
}
