static SavuRmp *savu_rmp_new(void) {
	SavuRmp *rmp = g_malloc0(sizeof(SavuRmp));
	rmp->key_file = g_key_file_new();
	return rmp;
}

void savu_rmp_free(SavuRmp *rmp) {
	if (rmp) {
		g_key_file_free(rmp->key_file);
		g_free(rmp);
	}
}

SavuRmp *savu_rmp_dup(SavuRmp const *src) {
	SavuRmp *dest = g_malloc0(sizeof(SavuRmp));
	guint i;

	dest->key_file = roccat_key_file_dup(src->key_file);
	dest->modified_rmp = src->modified_rmp;
	dest->modified_general = src->modified_general;
	dest->modified_buttons = src->modified_buttons;
	for (i = 0; i < SAVU_BUTTON_NUM; ++i)
		dest->modified_macros[i] = src->modified_macros[i];

	return dest;
}

SavuRmp *savu_rmp_read_with_path(gchar const *path, GError **error) {
	SavuRmp *rmp = savu_rmp_new();

	if (!g_key_file_load_from_file(rmp->key_file, path, G_KEY_FILE_NONE, error)) {
		savu_rmp_free(rmp);
		return NULL;
	}

	return rmp;
}

gboolean savu_rmp_write_with_path(gchar const *path, SavuRmp *rmp, GError **error) {
	gchar *data;
	gsize size;
	gboolean ret;

	data = g_key_file_to_data(rmp->key_file, &size, error);
	if (!data)
		goto error;

	ret = roccat_profile_write_with_path(path, data, size, error);
	g_free(data);
	if (!ret)
		goto error;

	return TRUE;
error:
	g_warning(_("Could not write rmp %s: %s"), path, (*error)->message);
	return FALSE;
}

SavuRmp const *savu_default_rmp(void) {
	static SavuRmp *rmp = NULL;
	GError *error = NULL;

	if (rmp == NULL) {
		rmp = savu_rmp_new();

		if (!g_key_file_load_from_data(rmp->key_file, (gchar const *)default_rmp_data, sizeof(default_rmp_data), 0, &error)) {
			g_clear_pointer(&rmp, savu_rmp_free);
			g_critical(_("Could not create default rmp: %s"), error->message);
			g_error_free(error);
		}
	}

	return rmp;
}
